INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (1, 'Салон красоты «Венера»', 'Внимательные, квалифицированные специалисты ознакомятся с вашей проблемой, узнают об имеющихся пожеланиях, составят индивидуальную программу, куда включат эффективные процедуры, направленные на достижение положительного результата. В заведении вы можете проконсультироваться с трихологом, косметологом, воспользоваться аппаратной, эстетической, инъекционной косметологией. Сотрудники занимаются лечением акне и постакне, целлюлита, увеличением губ, устранением мимических морщин, коррекцией фигуры, улучшением цвета лица, омоложением кожи. Также здесь выполняют эпиляцию, фотоэпиляцию, оформляют брови, делают различные виды массажей. Для всех работ мастера используют импортные, сертифицированные средства, сохраняющие на долгое время полученные эффекты. Команда центра регулярно повышает профессиональный уровень, проходит обучение, где знакомится с популярными, актуальными косметологическими новинками, применение которых значительно улучшает внешний вид клиентов.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Хмельницкого, 160', 4.1, 'MASTER', 8);
INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (2, 'Студия загара «Совершенство»', 'Жизнь в мегаполисе откладывает свои отпечатки – каждый чувствует усталость, ежедневно подвергается стрессам, сталкивается с другими негативными эмоциями. Восстановиться физически и духовно, привести в порядок внешний вид вам поможет посещение салона — уютного и комфортного заведения, где представлены лучшие косметологические процедуры. Специалисты угостят вас чашечкой ароматного чая или кофе, узнают об имеющихся мечтах, предложат разнообразные варианты их претворения в жизнь. Здесь вы можете оценить преимущество врачебной косметологии, воспользоваться инъекционными и аппаратными методиками, программой, направленной на коррекцию фигуры, сделать аккуратный маникюр и педикюр, поухаживать за волосами. Различные пилинги, маски, подобранные по типу кожи, массажи для лица устранят эстетические недостатки. Справиться с морщинами позволит нитевой лифтинг, плацентарная терапия LAENNEC, мезотерапия, биоревитализация. Косметологи салона для достижения превосходных результатов применяют новейшее оборудование, инновационные и классические техники. Вас порадует теплая обстановка, доброжелательность коллектива, регулярно обновляющийся список акций и доступные цены. Начните меняться вместе с салоном, умеющим «замораживать» молодость, продлевать прекрасные мгновения.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Добролюбова, 184', 4.8, 'MASTER', 14);
INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (3, 'Салон красоты «Эконом»', 'В уютном, по-домашнему теплом пространстве вас встретит доброжелательная команда мастеров ногтевого сервиса, мастеров эпиляции, визажистов, стилистов, которая создаст утонченный повседневный или вечерний образ. Большинство услуг студии посвящены маникюру и педикюру. Здесь вы можете побаловать себя классическим, европейским, горячим маникюром, придать форму ногтям, сделать аппаратный, карбамидный, частичный педикюр, обработку стопы. Услуги по восстановлению, лечению и уходу от IBX, Vitagel, INKI PROFI укрепят ногтевую пластину, увлажнят кутикулу, помогут отрастить натуральные длинные ногти. Мастера украшают ногти художественной росписью, стразами, втирками, литьем, слайдерами, покрывают ногти лаками CND, O.P.I, Gelish, E.M.I. и другими. Кроме этого, в студии оформляют брови, делают экспресс-макияж, укладывают волосы, собирают их в простые и сложные прически. В кабинете эпиляции избавляют от нежелательных волос на теле, используя воск. Особое внимание персонал уделяют безопасности клиентов: стерилизует и дезинфицирует режущие инструменты, применяет одноразовые материалы, проводит процедуры в перчатках.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Фрунзе, 164', 2.6, 'MASTER', 1);
