INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (18, 'Клуб красоты «Эконом»', 'В заведении считают, что создание подходящего образа – это целое искусство, требующее полной отдачи от мастера и клиента. Эксперты салона внимательно выслушают ваши пожелания, дадут профессиональные рекомендации, вместе с вами примут окончательное решение. Изменить имидж помогут вечерний или дневной макияж, наращивание ресниц, оформление бровей, укладка волос, изящная прическа. Эпиляция сделает кожу гладкой и нежной, придаст вам уверенности в своих силах. Особое внимание команда уделяет маникюру и педикюру: выполняет женский, детский, мужской маникюр и педикюр в различных техниках, наращивает ногти акрилом и гелем, украшает их оригинальными рисунками, ручными росписями, втирками, наклейками, стразами и другими элементами. Чтобы достичь желаемого результата, здесь используют средства брендов CND, O.P.I, Gelish, E.M.I., одноразовые расходные материалы. Сотрудники строго соблюдают санитарно-гигиенические нормы, тщательно обрабатывают режущие инструменты и фрезы, проводят сухожаровую стерилизацию.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Кирова, 156', 3.6, 'SALON', 2);
INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (19, 'Фабрика «The Best»', 'В уютном, по-домашнему теплом пространстве вас встретит доброжелательная команда мастеров ногтевого сервиса, мастеров эпиляции, визажистов, стилистов, которая создаст утонченный повседневный или вечерний образ. Большинство услуг студии посвящены маникюру и педикюру. Здесь вы можете побаловать себя классическим, европейским, горячим маникюром, придать форму ногтям, сделать аппаратный, карбамидный, частичный педикюр, обработку стопы. Услуги по восстановлению, лечению и уходу от IBX, Vitagel, INKI PROFI укрепят ногтевую пластину, увлажнят кутикулу, помогут отрастить натуральные длинные ногти. Мастера украшают ногти художественной росписью, стразами, втирками, литьем, слайдерами, покрывают ногти лаками CND, O.P.I, Gelish, E.M.I. и другими. Кроме этого, в студии оформляют брови, делают экспресс-макияж, укладывают волосы, собирают их в простые и сложные прически. В кабинете эпиляции избавляют от нежелательных волос на теле, используя воск. Особое внимание персонал уделяют безопасности клиентов: стерилизует и дезинфицирует режущие инструменты, применяет одноразовые материалы, проводит процедуры в перчатках.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Пушкина, 149', 2.8, 'MASTER', 4);
