INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (31, 'Фабрика красоты «Марлен»', 'Профессиональная команда поможет вам преобразиться и измениться, пока вы наслаждаетесь ароматным кофе, вкусным чаем. Парикмахеры выполнят простое или сложное окрашивание, подстригут вас, уложат волосы, соберут их в вечернюю или свадебную прическу. Посетив косметологический кабинет, вы подкорректируете форму бровей, нарастите ресницы, сделаете восковую, сахарную, лазерную эпиляцию, пилинг, обертывание, воспользуетесь кавитацией, позволяющей избавиться от целлюлита, жировых отложений. Нейл-мастера владеют различными техниками выполнения маникюра и педикюра, занимаются оформлением ногтей, покрытием их гель-лаком. Для всех проводимых процедур в салоне используют косметологические материалы ведущих производителей, таких как OPI, Gelish, Creative, Gehwol, Beauty Image, Aravia, Minx Professional, Dolce Vita. Персонал беспокоится не только о качестве предоставляемых услуг, а также о здоровье клиентов, поэтому стерилизует все инструменты, проводит кварцевание открытого типа.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Пушкина, 51', 2.7, 'MASTER', 3);
