INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (256, 'Фабрика красоты «Клевер»', 'Внимательные, квалифицированные специалисты ознакомятся с вашей проблемой, узнают об имеющихся пожеланиях, составят индивидуальную программу, куда включат эффективные процедуры, направленные на достижение положительного результата. В заведении вы можете проконсультироваться с трихологом, косметологом, воспользоваться аппаратной, эстетической, инъекционной косметологией. Сотрудники занимаются лечением акне и постакне, целлюлита, увеличением губ, устранением мимических морщин, коррекцией фигуры, улучшением цвета лица, омоложением кожи. Также здесь выполняют эпиляцию, фотоэпиляцию, оформляют брови, делают различные виды массажей. Для всех работ мастера используют импортные, сертифицированные средства, сохраняющие на долгое время полученные эффекты. Команда центра регулярно повышает профессиональный уровень, проходит обучение, где знакомится с популярными, актуальными косметологическими новинками, применение которых значительно улучшает внешний вид клиентов.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Днепровского, 171', 3.6, 'MASTER', 4);
