INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (296, 'Салон «The Best»', 'В уютном, по-домашнему теплом пространстве вас встретит доброжелательная команда мастеров ногтевого сервиса, мастеров эпиляции, визажистов, стилистов, которая создаст утонченный повседневный или вечерний образ. Большинство услуг студии посвящены маникюру и педикюру. Здесь вы можете побаловать себя классическим, европейским, горячим маникюром, придать форму ногтям, сделать аппаратный, карбамидный, частичный педикюр, обработку стопы. Услуги по восстановлению, лечению и уходу от IBX, Vitagel, INKI PROFI укрепят ногтевую пластину, увлажнят кутикулу, помогут отрастить натуральные длинные ногти. Мастера украшают ногти художественной росписью, стразами, втирками, литьем, слайдерами, покрывают ногти лаками CND, O.P.I, Gelish, E.M.I. и другими. Кроме этого, в студии оформляют брови, делают экспресс-макияж, укладывают волосы, собирают их в простые и сложные прически. В кабинете эпиляции избавляют от нежелательных волос на теле, используя воск. Особое внимание персонал уделяют безопасности клиентов: стерилизует и дезинфицирует режущие инструменты, применяет одноразовые материалы, проводит процедуры в перчатках.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Ленина, 176', 1.4, 'SALON', 8);
