INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (326, 'Клуб «Chokolife»', 'Профессиональная команда поможет вам преобразиться и измениться, пока вы наслаждаетесь ароматным кофе, вкусным чаем. Парикмахеры выполнят простое или сложное окрашивание, подстригут вас, уложат волосы, соберут их в вечернюю или свадебную прическу. Посетив косметологический кабинет, вы подкорректируете форму бровей, нарастите ресницы, сделаете восковую, сахарную, лазерную эпиляцию, пилинг, обертывание, воспользуетесь кавитацией, позволяющей избавиться от целлюлита, жировых отложений. Нейл-мастера владеют различными техниками выполнения маникюра и педикюра, занимаются оформлением ногтей, покрытием их гель-лаком. Для всех проводимых процедур в салоне используют косметологические материалы ведущих производителей, таких как OPI, Gelish, Creative, Gehwol, Beauty Image, Aravia, Minx Professional, Dolce Vita. Персонал беспокоится не только о качестве предоставляемых услуг, а также о здоровье клиентов, поэтому стерилизует все инструменты, проводит кварцевание открытого типа.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Ленина, 146', 1.1, 'SALON', 15);
INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (327, 'Студия «Респект»', 'В заведении считают, что создание подходящего образа – это целое искусство, требующее полной отдачи от мастера и клиента. Эксперты салона внимательно выслушают ваши пожелания, дадут профессиональные рекомендации, вместе с вами примут окончательное решение. Изменить имидж помогут вечерний или дневной макияж, наращивание ресниц, оформление бровей, укладка волос, изящная прическа. Эпиляция сделает кожу гладкой и нежной, придаст вам уверенности в своих силах. Особое внимание команда уделяет маникюру и педикюру: выполняет женский, детский, мужской маникюр и педикюр в различных техниках, наращивает ногти акрилом и гелем, украшает их оригинальными рисунками, ручными росписями, втирками, наклейками, стразами и другими элементами. Чтобы достичь желаемого результата, здесь используют средства брендов CND, O.P.I, Gelish, E.M.I., одноразовые расходные материалы. Сотрудники строго соблюдают санитарно-гигиенические нормы, тщательно обрабатывают режущие инструменты и фрезы, проводят сухожаровую стерилизацию.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. проспект Карла Маркса, 84', 1.4, 'SALON', 8);
INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (328, 'Клуб «Эконом»', 'Жизнь в мегаполисе откладывает свои отпечатки – каждый чувствует усталость, ежедневно подвергается стрессам, сталкивается с другими негативными эмоциями. Восстановиться физически и духовно, привести в порядок внешний вид вам поможет посещение салона — уютного и комфортного заведения, где представлены лучшие косметологические процедуры. Специалисты угостят вас чашечкой ароматного чая или кофе, узнают об имеющихся мечтах, предложат разнообразные варианты их претворения в жизнь. Здесь вы можете оценить преимущество врачебной косметологии, воспользоваться инъекционными и аппаратными методиками, программой, направленной на коррекцию фигуры, сделать аккуратный маникюр и педикюр, поухаживать за волосами. Различные пилинги, маски, подобранные по типу кожи, массажи для лица устранят эстетические недостатки. Справиться с морщинами позволит нитевой лифтинг, плацентарная терапия LAENNEC, мезотерапия, биоревитализация. Косметологи салона для достижения превосходных результатов применяют новейшее оборудование, инновационные и классические техники. Вас порадует теплая обстановка, доброжелательность коллектива, регулярно обновляющийся список акций и доступные цены. Начните меняться вместе с салоном, умеющим «замораживать» молодость, продлевать прекрасные мгновения.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. проспект Карла Маркса, 184', 2.9, 'SALON', 7);
