INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (364, 'Студия загара «KLEVER»', 'Внимательные, квалифицированные специалисты ознакомятся с вашей проблемой, узнают об имеющихся пожеланиях, составят индивидуальную программу, куда включат эффективные процедуры, направленные на достижение положительного результата. В заведении вы можете проконсультироваться с трихологом, косметологом, воспользоваться аппаратной, эстетической, инъекционной косметологией. Сотрудники занимаются лечением акне и постакне, целлюлита, увеличением губ, устранением мимических морщин, коррекцией фигуры, улучшением цвета лица, омоложением кожи. Также здесь выполняют эпиляцию, фотоэпиляцию, оформляют брови, делают различные виды массажей. Для всех работ мастера используют импортные, сертифицированные средства, сохраняющие на долгое время полученные эффекты. Команда центра регулярно повышает профессиональный уровень, проходит обучение, где знакомится с популярными, актуальными косметологическими новинками, применение которых значительно улучшает внешний вид клиентов.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Тюленина, 37', 4.6, 'MASTER', 8);
INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (365, 'Центр «Совершенство»', 'Жизнь в мегаполисе откладывает свои отпечатки – каждый чувствует усталость, ежедневно подвергается стрессам, сталкивается с другими негативными эмоциями. Восстановиться физически и духовно, привести в порядок внешний вид вам поможет посещение салона — уютного и комфортного заведения, где представлены лучшие косметологические процедуры. Специалисты угостят вас чашечкой ароматного чая или кофе, узнают об имеющихся мечтах, предложат разнообразные варианты их претворения в жизнь. Здесь вы можете оценить преимущество врачебной косметологии, воспользоваться инъекционными и аппаратными методиками, программой, направленной на коррекцию фигуры, сделать аккуратный маникюр и педикюр, поухаживать за волосами. Различные пилинги, маски, подобранные по типу кожи, массажи для лица устранят эстетические недостатки. Справиться с морщинами позволит нитевой лифтинг, плацентарная терапия LAENNEC, мезотерапия, биоревитализация. Косметологи салона для достижения превосходных результатов применяют новейшее оборудование, инновационные и классические техники. Вас порадует теплая обстановка, доброжелательность коллектива, регулярно обновляющийся список акций и доступные цены. Начните меняться вместе с салоном, умеющим «замораживать» молодость, продлевать прекрасные мгновения.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Ломоносова, 120', 1.2, 'SALON', 10);
INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (366, 'Салон красоты «Бьюти»', 'В заведении считают, что создание подходящего образа – это целое искусство, требующее полной отдачи от мастера и клиента. Эксперты салона внимательно выслушают ваши пожелания, дадут профессиональные рекомендации, вместе с вами примут окончательное решение. Изменить имидж помогут вечерний или дневной макияж, наращивание ресниц, оформление бровей, укладка волос, изящная прическа. Эпиляция сделает кожу гладкой и нежной, придаст вам уверенности в своих силах. Особое внимание команда уделяет маникюру и педикюру: выполняет женский, детский, мужской маникюр и педикюр в различных техниках, наращивает ногти акрилом и гелем, украшает их оригинальными рисунками, ручными росписями, втирками, наклейками, стразами и другими элементами. Чтобы достичь желаемого результата, здесь используют средства брендов CND, O.P.I, Gelish, E.M.I., одноразовые расходные материалы. Сотрудники строго соблюдают санитарно-гигиенические нормы, тщательно обрабатывают режущие инструменты и фрезы, проводят сухожаровую стерилизацию.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Орджоникидзе, 142', 4.2, 'SALON', 5);
