INSERT INTO companies (id, name, description, timetable, site, phone, address, rating, company_type, owner_id) VALUES (887, 'Клуб красоты «Цветочек»', 'В заведении считают, что создание подходящего образа – это целое искусство, требующее полной отдачи от мастера и клиента. Эксперты салона внимательно выслушают ваши пожелания, дадут профессиональные рекомендации, вместе с вами примут окончательное решение. Изменить имидж помогут вечерний или дневной макияж, наращивание ресниц, оформление бровей, укладка волос, изящная прическа. Эпиляция сделает кожу гладкой и нежной, придаст вам уверенности в своих силах. Особое внимание команда уделяет маникюру и педикюру: выполняет женский, детский, мужской маникюр и педикюр в различных техниках, наращивает ногти акрилом и гелем, украшает их оригинальными рисунками, ручными росписями, втирками, наклейками, стразами и другими элементами. Чтобы достичь желаемого результата, здесь используют средства брендов CND, O.P.I, Gelish, E.M.I., одноразовые расходные материалы. Сотрудники строго соблюдают санитарно-гигиенические нормы, тщательно обрабатывают режущие инструменты и фрезы, проводят сухожаровую стерилизацию.', '09:00 - 18:00, без выходных', 'http://example.com', '+7 (999) 999-99-99', 'ул. Добролюбова, 71', 4.5, 'SALON', 5);
